public boolean validate()
{
    boolean ret = true;

    if(!fromDate)
        ret = checkFailed("Please enter From date");

    if(!toDate)
        ret = checkFailed("Please enter To date");

    if(fromDate > toDate)
    {
        ret = checkFailed("To date must be later than From date");
    }

    return ret;
}
