[Form]
public class SPYAbsenceJournal extends FormRun
{
    SPYAbsenceJournalTable  absenceJournalTable;
}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = SPYAbsenceJournalTable.canSubmitToWorkflow();

    return ret;
}

public void init()
{
    if(element.args().record() is SPYAbsenceJournalTable)
        absenceJournalTable = element.args().record() as SPYAbsenceJournalTable;

    super();

    element.setEnable();
}

public void setTitleText()
{
    headerTitle.text(strFmt('%1 : %2', SPYAbsenceJournalTable.JournalCode,
                                           SPYAbsenceJournalTable.Description));
}

public int active()
{
    int ret;

    ret = super();

    element.setTitleText();
    element.setEnable();

    return ret;
}

void clicked()
{
    super();

    element.setEnable();
    SPYAbsenceJournalTrans_ds.executeQuery();
    SPYAbsenceJournalTable_ds.research(true);
}

public void tabChanged(int _FromTab, int _ToTab)
