public SysIFilter parmFilter()
{
    SysIFilter filter = SysFilterFactory::createEmptyFilter();

    SysIFilterValue curUserIdFilterValue = SysFilterValueFactory::createFilterValueForString(curUserId(), extendedTypeNum(WorkflowUser));
    filter.addItem(curUserIdFilterValue, SysFilterOperator::Equal, SysFilterValueResolutionMethod::None);

    SysIFilterValue personFilterValue = SysFilterValueFactory::createFilterValueForInteger(HcmWorkerLookup::currentPerson(), extendedTypeNum(HcmPersonRecId));
    filter.addItem(personFilterValue, SysFilterOperator::Equal, SysFilterValueResolutionMethod::None);

    SysIFilterValue workerValue = SysFilterValueFactory::createFilterValueForInteger(currentWorker.RecId, extendedTypeNum(HcmWorkerRecId));
    filter.addItem(workerValue, SysFilterOperator::Equal, SysFilterValueResolutionMethod::None);

    HcmPosition primaryPos = HcmPosition::findByPosition(currentWorker.primaryPositionId());
    SysIFilterValue hcmPositionFilterValue = SysFilterValueFactory::createFilterValueForInteger(primaryPos.RecId, extendedTypeNum(HcmPositionInPositionHierarchyPosition));
    filter.addItem(hcmPositionFilterValue, SysFilterOperator::Equal, SysFilterValueResolutionMethod::None);

    return filter;
}

public MssManagerReportsHierarchyViewModel parmManagerReportsHierarchyModel()
{
    return managerReportsHierarchyModel;
}

public void init()
{
    currentWorker = HcmWorker::find(HcmWorkerLookup::currentWorker());

    if (!currentWorker)
    {
        throw error("@HCM:EssNoUserAssociation");
    }

    this.bindManagerReportsHierarchyViewModel(MssManagerReportsHierarchyViewModel::newManagerReportsHierarchy(currentWorker));

    super();

    changeEvent = SysFilterChangeEvent::construct();

    element.design().caption(strFmt("@HCM:WelcomeName", DirPartyTable::findRec(currentWorker.Person).Name));

    HcmSharedParameters parameters = HcmSharedParameters::find();
    isPositionPersonnelActionEnabled = parameters.PersonnelActionPosition;
    isWorkerPersonnelActionEnabled = parameters.PersonnelActionWorker;
    this.initPersonnelActionControls();

    element.setPerformanceMetrics();
    element.setDiscussionMetrics();

    element.setAttachmentsDetail();

    this.initManagerControls();

    EssCommonHelper::hideSystemDefinedAttachmentButton(element);
    EssCommonHelper::hideSystemDefinedAdvancedFilterButton(element);

    HRMParameters hrmParameters = HRMParameters::find();
    this.showOpenPositionsTile(hrmParameters.MssOpenPositionsType);
    MssExitingWorkers.visible(hrmParameters.MssViewExitingWorkerOption != HcmViewExitingWorkerOption::ViewNone);
}

