private void runMenuItem(Common _tableBuffer, str _menuItemName, boolean _lookupRecord = true, str _parameter = "")
{
    Args args = new Args();

    if (_lookupRecord)
    {
        args.lookupRecord(_tableBuffer);
    }
    else
    {
        args.record(_tableBuffer);
    }

    args.parm(_parameter);
    args.openMode(OpenMode::Auto);
    args.caller(this);

    new MenuFunction(_menuItemName, MenuItemType::Display).run(args);
}

private void getSkillCount()
{
    select count(RecId) from HcmPersonSkill where
            HcmPersonSkill.Person == currentWorker.Person;

    SkillsCount.value(HcmPersonSkill.RecId);
}

/// <summary>
/// Sets the number and the latest update time of external attachments for the current worker record.
/// </summary>
private void setAttachmentsDetail()
{
    DocuRef workerRef;
    select count(RecId) from workerRef
        where workerRef.RefRecId == currentWorker.RecId
            && workerRef.RefTableId == tablenum(HcmWorker)
            && workerRef.Restriction == DocuRestriction::External;

    AttachmentsCountExternal.value(workerRef.RecId);

    if (workerRef.RecId)
    {
        select firstonly Name, createdDateTime from workerRef
            order by workerRef.createdDateTime desc
            where workerRef.RefRecId == currentWorker.RecId
                && workerRef.RefTableId == tablenum(HcmWorker)
                && workerRef.Restriction == DocuRestriction::External;

        MostRecentAttachmentName.text(workerRef.Name);
        MostRecentUploadTime.dateTimeValue(DateTimeUtil::applyTimeZoneOffset(workerRef.createdDateTime, DateTimeUtil::getCompanyTimeZone()));           
    }

    MostRecentAttachmentName.visible(workerRef.RecId != 0);
    MostRecentUploadTime.visible(workerRef.RecId != 0);

}

private void setPerformanceMetrics()
{
    QueryRun     performanceGoalQueryRun = new QueryRun(queryStr(EssWorkerCountPerformanceGoalsByStatus));
    str notStartedText                   = strFmt("@HCM:NotStartedCount", 0);
    str onTrackText                      = strFmt("@HCM:OnTrackCount", 0);
    str needsImprovementText             = strFmt("@HCM:NeedsImprovementCount", 0);


    while(performanceGoalQueryRun.next())
    {
        HcmGoal goal = performanceGoalQueryRun.get(tableNum(HcmGoal));
        switch (goal.Status)
        {
            case HRMGoalStatus::NotStarted:
                notStartedText = strFmt("@HCM:NotStartedCount",any2Str(goal.RecId));
                break;
            case HRMGoalStatus::OnTrack:
                onTrackText = strFmt("@HCM:OnTrackCount",any2Str(goal.RecId));
                break;
            case HRMGoalStatus::NeedsImprovement:
                needsImprovementText = strFmt("@HCM:NeedsImprovementCount",any2Str(goal.RecId));
                break;
        }
    }

    NotStarted.text(notStartedText);
    OnTrack.text(onTrackText);
    NeedsImprovement.text(needsImprovementText);

}

private void setDiscussionMetrics()
{
    QueryRun discussionQueryRun = new QueryRun(queryStr(EssDiscussionCountByStatus));
    int inProgressCount = 0;
    int readyForReviewCount = 0;
    int finalReviewCount = 0;

    while(discussionQueryRun.next())
    {
        HcmDiscussion discussion = discussionQueryRun.get(tableNum(HcmDiscussion));
        switch (discussion.DiscussionApprovalStatus)
        {
            case HcmDiscussionApproveStatus::InProgress:
                inProgressCount = int642int(discussion.RecId);
                break;
            case HcmDiscussionApproveStatus::ReadyForReview:
                readyForReviewCount = int642int(discussion.RecId);
                break;
            case HcmDiscussionApproveStatus::FinalReview:
                finalReviewCount = int642int(discussion.RecId);
                break;
        }
    }

    InProgress.text(strFmt("@Talent:ReviewsInProgress", int2Str(inProgressCount)));
    ReadyForReview.text(strFmt("@Talent:ReviewsReadyForReview", int2Str(readyForReviewCount)));
    FinalReview.text(strFmt("@Talent:ReviewsFinalReview", int2Str(finalReviewCount)));
}

