/// <summary>
/// This method is used as a callback on forms opened from the workspace.
/// This is used in the various child forms to refresh this form upon exiting.
/// </summary>
public void refreshFormCallback()
{
    EssWorkerView_DS.research(true);
    Manager_DS.research(true);

    HcmPersonCertificate_DS.research();

    if (!HcmPersonCertificate)
    {
       element.setCertificateFields();
    }

    element.getSkillCount();
    this.refreshingFormCallback(element);
    element.setCoursesFields(true);
    element.setDiscussionFields(true);
    element.setPerformanceMetrics();
    element.setDiscussionMetrics();
    element.setAttachmentsDetail();
}

