private void setCertificateFields()
{
    expiringPersonCertificate = null;
    expiredPersonCertificate = null;        
    boolean noCertificate = true;      
    date dateToCompare = HcmDateTimeUtil::currentDate();

    // The HcmPersonCertificate buffer is expected to be sorted by field ExpirationDate in ascending order
    HcmPersonCertificate personCertificate = HcmPersonCertificate_DS.getFirst();        
    while (personCertificate)
    {          
        noCertificate = false;

        if (personCertificate.ExpirationDate != dateNull())
        {
            if (personCertificate.ExpirationDate >= dateToCompare)
            {
                expiringPersonCertificate = personCertificate;
                break;
            }
            else
            {
                expiredPersonCertificate = personCertificate;
            }
        }

        personCertificate = HcmPersonCertificate_DS.getNext();
    }

    if (!expiringPersonCertificate && !expiredPersonCertificate )
    {
        CertificateMessage.visible(true);
        ExpiredCertificate_Description.visible(false);
        ExpiredCertificateMessage.visible(false);
        ExpiringCertificate_Description.visible(false);
        ExpiringCertificateMessage.visible(false);

        if (noCertificate)
        {
            CertificateMessage.text("@HCM:NoCertificates");
        }
        else
        {
            CertificateMessage.text("@HCM:NoCertificatesExpiring");
        }
    }
    else 
    {
        CertificateMessage.visible(false);

        if (expiringPersonCertificate)
        {
            ExpiringCertificate_Description.visible(true);
            ExpiringCertificateMessage.visible(true);
            ExpiringCertificate_Description.text(HcmCertificateType::find(expiringPersonCertificate.CertificateType).Description);
            ExpiringCertificateMessage.text(strFmt("@HCM:ExpiringInCountOfDays",  expiringPersonCertificate.ExpirationDate - dateToCompare));
        }
        else
        {
            ExpiringCertificate_Description.visible(false);
            ExpiringCertificateMessage.visible(false);
        }

        if (expiredPersonCertificate)
        {  
            ExpiredCertificate_Description.visible(true);
            ExpiredCertificateMessage.visible(true);
            ExpiredCertificate_Description.text(HcmCertificateType::find(expiredPersonCertificate.CertificateType).Description);
            ExpiredCertificateMessage.text(strFmt("@HCM:ExpiredCountOfDaysPast", dateToCompare - expiredPersonCertificate.ExpirationDate));
        }
        else
        {
            ExpiredCertificate_Description.visible(false);
            ExpiredCertificateMessage.visible(false);
        }
    }

    ViewAllCertificates.visible(!noCertificate);
    AddACertificate.visible(noCertificate);
}

/// <summary>
/// Set the visibility for the discussion card
/// </summary>
/// <param name = "refresh">true if the datasource needs to be refreshed</param>
private void setDiscussionFields(boolean refresh = false)
{
    if (refresh)
