        HcmDiscussion_DS.executeQuery();
    }

    boolean hasRecord = HcmDiscussion.RecId != 0;

    HcmDiscussion_Description.visible(hasRecord);
    HcmDiscussion_DiscussionType.visible(hasRecord);
    NextDiscussionDateTime.visible(hasRecord);

    HcmDiscussion_DiscussionType.text(HcmDiscussionType.DiscussionTypeId);
}

/// <summary>
/// Set the visibility for the course card
/// </summary>
private void setCoursesFields(boolean refresh = false)
{
    if (refresh)
    {
        HrmCourseAttendee_DS.executeQuery();
        HrmCourseTable_DS.executeQuery();
    }

    boolean recordFound = HrmCourseTable_DS.cursor().RecId != 0 && HrmCourseAttendee_DS.cursor().RecId != 0;

    description.visible(recordFound);
    DateTimeGroup.visible(recordFound);
    CourseNotFound.visible(!recordFound);
}

