private void setTabFocusForManager()
{
    if (ManagerTabPage.visible())
    {
        // Since form extensions can add tab pages that the base form is unaware of, dynamically determine index for setting tab focus
        str tabControlName = formControlStr(EssWorkspace, FormTabControl);
        FormTabControl tabControl = element.design().controlName(tabControlName) as FormTabControl;

        for (int i = 1; i <= tabControl.controlCount(); i++)
        {
            FormTabPageControl control = tabControl.controlNum(i) as FormTabPageControl;

            if (control && control.name() == ManagerTabPage.name())
            {
                FormTabControl.tab(i);
                break;
            }
        }
    }
}

/// <summary>
/// Refresh the highlighted manager based on manager reports hierarchy view-model changes.
/// </summary>
public void handleHierarchyUpdated()
{
    HcmWorkerRecId newManagerRecId = managerReportsHierarchyModel.getManager().RecId;
    if (newManagerRecId == currentWorker.RecId)
    {
        ManagerCard.visible(false);
    }
    else
    {
        ManagerCard.visible(true);
    }

    Manager_ds.queryBuildDataSource().clearRanges();
    Manager_ds.queryBuildDataSource().addRange(fieldNum(HcmWorker, RecId)).value(queryValue(newManagerRecId));
    Manager_ds.executeQuery();
}

