display container displayImage()
{
    if (!personImage)
    {
        if (EssWorkerView.Image)
        {
            personImage = EssWorkerView.Image;
        }
        else
        {
            // If the user doesn't have a default profile picture, display the person silhouette
            ImageReference imgRef = ImageReference::constructForSymbol(ImageReferenceSymbol::Person);
            personImage = imgRef.pack();
        }
    }

    return personImage;
}

