public void init()
{
    super();

    QueryBuildDataSource qbds = this.queryBuildDataSource();

    // Include those courses where the worker is either registered or has been a confirmed participant
    QueryBuildRange statusRange = qbds.addRange(fieldNum(HrmCourseAttendee, status));
    statusRange.value(queryValue(HRMCourseAttendeeStatus::Registered));

    statusRange = qbds.addRange(fieldNum(HrmCourseAttendee, status));
    statusRange.value(queryValue(HRMCourseAttendeeStatus::Confirmation));

    statusRange.status(RangeStatus::Hidden);
}

public void init()
{
    super();

    QueryBuildDataSource qbds = HRMCourseTable_DS.queryBuildDataSource();
    qbds.firstOnly(true);
    qbds.addSortField(fieldNum(HRMCourseTable, startDateTime));

    QueryBuildRange qbr = qbds.addRange(fieldNum(HRMCourseTable, startDateTime));
    //doing date math here because >= doesn't work in the query.
    qbr.value('>' + queryvalue(DateTimeUtil::addDays(DateTimeUtil::utcNow(), -1)));
    qbr.status(RangeStatus::Hidden);

    element.setCoursesFields();
}

