    element.setCoursesFields();

    return ret;
}

public void init()
{
    super();

    str todayDate = date2StrXpp(HcmDateTimeUtil::currentDate());

    QueryBuildRange validFromRange = HrmCompFixedEmpl_DS.queryBuildDataSource().addRange(fieldNum(HrmCompFixedEmpl, ValidFrom));
    validFromRange.value(strFmt('(ValidFrom <= %1)', todayDate));
    validFromRange.status(RangeStatus::Hidden);

    QueryBuildRange validToRange = HrmCompFixedEmpl_DS.queryBuildDataSource().addRange(fieldNum(HrmCompFixedEmpl, ValidTo));
    validToRange.value(strFmt('(ValidTo >= %1)', todayDate));
    validToRange.status(RangeStatus::Hidden);
}

