[Form]
public class SPYOvertimeJournal extends FormRun
{
    SPYOvertimeJournalTable           overtimeJournalTableCaller;
    NumberSeqFormHandler              numberSeqFormHandler;
}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = SPYOvertimeJournalTable.canSubmitToWorkflow();

    return ret;
}

public void init()
{
    if(element.args().record() is SPYOvertimeJournalTable)
        overtimeJournalTableCaller = element.args().record() as SPYOvertimeJournalTable;

    super();

    element.setEnable();
}

NumberSeqFormHandler numberSeqFormHandler()
{
    if (!numberSeqFormHandler)
    {
        numberSeqFormHandler = NumberSeqFormHandler::newForm(SPYPayrollParameters::numRefOvertimeJournalCode().NumberSequenceId,
                                                             element,
                                                             SPYOvertimeJournalTable_ds,
                                                             fieldNum(SPYOvertimeJournalTable,OvertimeJournalCode)
                                                            );
    }
    return numberSeqFormHandler;
}

void setEnable()
{
    boolean allowEdit;

    SPYOvertimeJournalValidate.enabled(!SPYOvertimeJournalTable.Posted);
    SPYOvertimeJournalPost.enabled(!SPYOvertimeJournalTable.Posted);
    SPYOvertimeLedger.enabled(SPYOvertimeJournalTable.Posted);
    allowEdit = (SPYOvertimeJournalTable.WorkflowStatus == SPYWorkflowStatus::Created) ||
                (SPYOvertimeJournalTable.WorkflowStatus == SPYWorkflowStatus::Rejected) ||
                (SPYOvertimeJournalTable.WorkflowStatus == SPYWorkflowStatus::RequestChange);
    SPYOvertimeJournalTable_ds.allowEdit(allowEdit);
    SPYOvertimeJournalTable_ds.allowDelete(allowEdit);
    SPYOvertimeJournalTrans_ds.allowCreate(allowEdit);
    SPYOvertimeJournalTrans_ds.allowEdit(allowEdit);
    SPYOvertimeJournalTrans_ds.allowDelete(allowEdit);
}

public int active()
{
    int ret;

    ret = super();

    element.setTitleText();
    element.setEnable();

    return ret;
}

public boolean validateWrite()
{
    boolean         ret;

    ret = super();
    ret = element.numberSeqFormHandler().formMethodDataSourceValidateWrite(ret) && ret;

    return ret;
}

