public void init()
{
    if(element.args().record() is SPYPayrollEarningsJournalTable)
        payrollEarningsJournalTable = element.args().record() as SPYPayrollEarningsJournalTable;

    super();



    element.setEnable();
}

public SPYPayrollParameterState payrollParameterState()
{
    container                           workerContainer;
    SPYPayrollEarningsJournalTrans      payrollEarningsJournalTrans;
    SPYPayrollParameterState            payrollParameterState = new SPYPayrollParameterState();
    container                           payrollLedgerCon;
    SPYPayrollLedger                    payrollLedger;

    while select worker from payrollEarningsJournalTrans group by worker
        where payrollEarningsJournalTrans.PayrollEarningsJournal == SPYPayrollEarningsJournalTable.RecId
    {
        workerContainer = conPoke(workerContainer,conLen(workerContainer)+1,payrollEarningsJournalTrans.Worker);
    }
    while select RecId from payrollLedger
        join RecId from payrollEarningsJournalTrans
        where payrollEarningsJournalTrans.RecId == payrollLedger.PayrollEarningsJournalTrans &&
              payrollEarningsJournalTrans.PayrollEarningsJournal == SPYPayrollEarningsJournalTable.RecId
    {
        payrollLedgerCon = conpoke(payrollLedgerCon,conlen(payrollLedgerCon) + 1,payrollLedger.RecId);
    }
    payrollParameterState.parmPayrollLedgerCon(payrollLedgerCon);
    payrollParameterState.parmWorkerContainer(workerContainer);
    payrollParameterState.parmCalledFromJournal(true);
    payrollParameterState.parmSourceDocument(SPYPayrollEarningsJournalTable.TableId);
    payrollParameterState.parmSourceDocumentRef(SPYPayrollEarningsJournalTable.RecId);
    return payrollParameterState;
}

