[Form]
public class SPYPayrollJournal extends FormRun
{
    SPYPayrollJournalTable                  payrollJournalTableCaller;

}

public boolean canSubmitToWorkflow()
{
    boolean ret;

    ret = SPYPayrollJournalTable.canSubmitToWorkflow();

    return ret;
}

public void init()
{
    if(element.args().record() is SPYPayrollJournalTable)
        payrollJournalTableCaller = element.args().record() as SPYPayrollJournalTable;

    super();

    element.setEnable();
}

void setEnable()
{
    boolean     isWorkflowEnabled = WorkflowTable::findActiveTemplate(workflowtypestr(SPYPayrollJour)) ? true : false;
    boolean     checkEditable = ((SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::Created ||
                                SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::Submitted ||
                                SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::Started ||
                                SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::RequestChange) && !isWorkflowEnabled) &&
                                !SPYPayrollJournalTable.Posted ?
                                true : false;

    SPYPayrollJournalTable_ds.allowEdit(checkEditable);
    SPYPayrollJournalTable_ds.allowDelete(SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::Created);
    /*
    SPYPayrollJournalLine_ds.allowCreate(SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::Created);
    SPYPayrollJournalLine_ds.allowEdit(SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::Created);
    SPYPayrollJournalLine_ds.allowDelete(SPYPayrollJournalTable.WorkflowStatus == SPYWorkflowStatus::Created);
    */
    PayrollJourPost.enabled(!SPYPayrollJournalTable.Posted);
    buttonVoucherTrans.enabled(SPYPayrollJournalTable.Posted);
    SubLedgerJournal.enabled(!SPYPayrollJournalTable.Posted);        
}

public void setEnableLine()
{
    if(!SPYPayrollJournalTable.Posted && SPYPayrollJournalLine.DistributeCost)
        AccountingDistributionsLineButton.enabled(true);
    else
        AccountingDistributionsLineButton.enabled(false);
}

public void setTitleText()
{
    headerTitle.text(strFmt('%1 : %2', SPYPayrollJournalTable.JournalId,
                            SPYPayrollJournalTable.Description));
}

public int active()
{
    int ret;

    ret = super();

    element.setTitleText();
    element.setEnable();

    return ret;
}

